int h1 = foo_start();
int h2 = bar_start(h1);
int h3 = baz_start(h2);
hclose(h3); /* baz, bar and foo are shut down */

/* create stack of two protocols */
h1 = foo_start();
h2 = bar_start(h1);
/* top protocol is closed but bottom one is still alive */
h1 = bar_stop(h2);
/* new top protocol is started */
h3 = baz_start(h1);
/* shut down both protocols */
h1 = baz_stop(h3);
foo_stop(h1);

