int tcps = tcp_connect("192.168.0.111:80");
/* Websockets is a connected protocol. */
int ws = websock_connect(tcps);
uint16_t compression_algoritm;
mrecv(ws, &compression_algorithm, 2, -1);
/* Compression socket is unconnected. */
